// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./LMSRPredictionMarket.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./PredictionMarketPositions.sol"; // Import the ERC-1155 contract

contract MarketFactory is Ownable {
    address[] public activeMarkets;
    PredictionMarketPositions public positions;
    // Mapping to store market titles
    mapping(address => string) public marketTitles;

    event MinterAuthorized(address indexed marketAddress);
    event MarketCreated(address indexed marketAddress, string title);
    event RolesGranted(address indexed marketAddress);
    event FundsTransferred(address indexed marketAddress, uint256 amount);

    /**
     * @dev Constructor to initialize the factory with the ERC-1155 positions contract
     * @param baseURI URI of positions address
     */

    constructor(string memory baseURI) Ownable(msg.sender) {
        positions = new PredictionMarketPositions(baseURI, address(this));
    }

    function createMarket(
        uint256 marketId,
        string memory title,
        string[] memory outcomes,
        address oracle,
        uint256 b,
        uint256 duration,
        uint256 feePercent,
        address feeRecipient,
        address tokenAddress,
        uint256 initialFunds
    ) public returns (address) {
        // Deploy the LMSRPredictionMarket contract

        LMSRPredictionMarket newMarket = new LMSRPredictionMarket(
            marketId,
            title,
            outcomes,
            oracle,
            b,
            duration,
            feePercent,
            feeRecipient,
            tokenAddress,
            initialFunds,
            address(positions)

        );

        // Add the new market to the list of active markets
        activeMarkets.push(address(newMarket));

        marketTitles[address(newMarket)] = title;
        emit MarketCreated(address(newMarket), title);

        // Grant roles to this contract
        positions.grantRole(positions.MINTER_ROLE(), address(newMarket));
        positions.grantRole(positions.BURNER_ROLE(), address(newMarket));
        emit RolesGranted(address(newMarket));

        if (initialFunds > 0) {
            require(
                IERC20(tokenAddress).transferFrom(
                    msg.sender,
                    address(newMarket),
                    initialFunds
                ),
                "Initial fund transfer failed"
            );
        }
    // Transfer ownership of the deployed market to the creator
        newMarket.transferOwnership(msg.sender);

        return address(newMarket);
    }

    /**
     * @dev Returns the list of all active markets
     */
    function getActiveMarkets() public view returns (address[] memory) {
        return activeMarkets;
    }

    function getPositions() public view returns (address) {
    return address(positions);
}

}

src/core/PredictionMarketPositions.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract PredictionMarketPositions is ERC1155, Ownable, AccessControl {
    // Base URI for token metadata
    string private baseURI;
    // Define roles
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");

    event TokensMinted(address indexed to, uint256 indexed tokenId, uint256 amount);
    event TokensBurned(address indexed from, uint256 indexed tokenId, uint256 amount);


    constructor(string memory _uri, address _initialOwner) ERC1155(_uri) Ownable(_initialOwner) {
        // Metadata URI for each token
        baseURI = _uri;
        // Grant roles to the initial owner
        _grantRole(DEFAULT_ADMIN_ROLE, _initialOwner);
        _grantRole(MINTER_ROLE, _initialOwner);
        _grantRole(BURNER_ROLE, _initialOwner);

    }
    // Override supportsInterface to resolve conflict between AccessControl and ERC1155
    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC1155, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    /**
     * @notice Mint tokens for a specific market and outcome.
     * @dev Only accounts with MINTER_ROLE can call this function.
     */

    function mint(address to, uint256 marketId, uint256 outcomeIndex, uint256 amount) external onlyRole(MINTER_ROLE)  {
        uint256 tokenId = _getTokenId(marketId, outcomeIndex); // Create unique token ID
        _mint(to, tokenId, amount, "");
        emit TokensMinted(to, tokenId, amount);
    }

    /**
     * @notice Burn tokens for a specific market and outcome.
     * @dev Only accounts with BURNER_ROLE can call this function.
     */

    function burn(address from, uint256 marketId, uint256 outcomeIndex, uint256 amount) external onlyRole(BURNER_ROLE) {
        require(from != address(0), "Cannot burn from zero address");
        require(amount > 0, "Burn amount must be greater than zero");
        uint256 tokenId = _getTokenId(marketId, outcomeIndex);
        _burn(from, tokenId, amount);
        emit TokensBurned(from, tokenId, amount);
    }
    /**
     * @notice Internal function to compute a unique token ID based on marketId and outcomeIndex.
     */
    function _getTokenId(uint256 marketId, uint256 outcomeIndex) internal pure returns (uint256) {
        return uint256(keccak256(abi.encode(marketId, outcomeIndex)));
    }

    /**
     * @notice Public function to get the token ID for a specific market and outcome.
     */

    // Add a public function to get the token ID
    function getTokenId(uint256 marketId, uint256 outcomeIndex) external pure returns (uint256) {
        return _getTokenId(marketId, outcomeIndex);
    }

    /**
     * @notice Override URI function to return metadata URI for a token.
     */
    function uri(uint256 tokenId) public view override returns (string memory) {
        return string(abi.encodePacked(baseURI, Strings.toString(tokenId), ".json"));
    }



}

src/utils/ABDKMath64x64.sol.content:
// SPDX-License-Identifier: BSD-4-Clause
/*
 * ABDK Math 64.64 Smart Contract Library.  Copyright © 2019 by ABDK Consulting.
 * Author: Mikhail Vladimirov <mikhail.vladimirov@gmail.com>
 */
pragma solidity ^0.8.0;

/**
 * Smart contract library of mathematical functions operating with signed
 * 64.64-bit fixed point numbers.  Signed 64.64-bit fixed point number is
 * basically a simple fraction whose numerator is signed 128-bit integer and
 * denominator is 2^64.  As long as denominator is always the same, there is no
 * need to store it, thus in Solidity signed 64.64-bit fixed point numbers are
 * represented by int128 type holding only the numerator.
 */
library ABDKMath64x64 {
    /*
    * Minimum value signed 64.64-bit fixed point number may have. 
    */
    int128 private constant MIN_64x64 = -0x80000000000000000000000000000000;

    /*
    * Maximum value signed 64.64-bit fixed point number may have. 
    */
    int128 private constant MAX_64x64 = 0x7FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF;

    /**
     * Convert signed 256-bit integer number into signed 64.64-bit fixed point
     * number.  Revert on overflow.
     *
     * @param x signed 256-bit integer number
     * @return signed 64.64-bit fixed point number
     */
    function fromInt(int256 x) internal pure returns (int128) {
        unchecked {
            require(x >= -0x8000000000000000 && x <= 0x7FFFFFFFFFFFFFFF);
            return int128(x << 64);
        }
    }

    /**
     * Convert signed 64.64 fixed point number into signed 64-bit integer number
     * rounding down.
     *
     * @param x signed 64.64-bit fixed point number
     * @return signed 64-bit integer number
     */
    function toInt(int128 x) internal pure returns (int64) {
        unchecked {
            return int64(x >> 64);
        }
    }

    /**
     * Convert unsigned 256-bit integer number into signed 64.64-bit fixed point
     * number.  Revert on overflow.
     *
     * @param x unsigned 256-bit integer number
     * @return signed 64.64-bit fixed point number
     */
    function fromUInt(uint256 x) internal pure returns (int128) {
        unchecked {
            require(x <= 0x7FFFFFFFFFFFFFFF);
            return int128(int256(x << 64));
        }
    }

    /**
     * Convert signed 64.64 fixed point number into unsigned 64-bit integer
     * number rounding down.  Revert on underflow.
     *
     * @param x signed 64.64-bit fixed point number
     * @return unsigned 64-bit integer number
     */
    function toUInt(int128 x) internal pure returns (uint64) {
        unchecked {
            require(x >= 0);
            return uint64(uint128(x >> 64));
        }
    }

    /**
     * Convert signed 128.128 fixed point number into signed 64.64-bit fixed point
     * number rounding down.  Revert on overflow.
     *
     * @param x signed 128.128-bin fixed point number
     * @return signed 64.64-bit fixed point number
     */
    function from128x128(int256 x) internal pure returns (int128) {
        unchecked {
            int256 result = x >> 64;
            require(result >= MIN_64x64 && result <= MAX_64x64);
            return int128(result);
        }
    }

    /**
     * Convert signed 64.64 fixed point number into signed 128.128 fixed point
     * number.
     *
     * @param x signed 64.64-bit fixed point number
     * @return signed 128.128 fixed point number
     */
    function to128x128(int128 x) internal pure returns (int256) {
        unchecked {
            return int256(x) << 64;
        }
    }

    /**
     * Calculate x + y.  Revert on overflow.
     *
     * @param x signed 64.64-bit fixed point number
     * @param y signed 64.64-bit fixed point number
     * @return signed 64.64-bit fixed point number
     */
    function add(int128 x, int128 y) internal pure returns (int128) {
        unchecked {
            int256 result = int256(x) + y;
            require(result >= MIN_64x64 && result <= MAX_64x64);
            return int128(result);
        }
    }

    /**
     * Calculate x - y.  Revert on overflow.
     *
     * @param x signed 64.64-bit fixed point number
     * @param y signed 64.64-bit fixed point number
     * @return signed 64.64-bit fixed point number
     */
    function sub(int128 x, int128 y) internal pure returns (int128) {
        unchecked {
            int256 result = int256(x) - y;
            require(result >= MIN_64x64 && result <= MAX_64x64);
            return int128(result);
        }
    }

    /**
